def useri(wordlist,final_bucket1,final_bucket2):
    sentences=[]
    da_list=[]
    for line in wordlist:
      #print(line)
      found=0
      list2b=[]
      sentences.append(line)
      for pattern in final_bucket1:
         ends=pattern.split()
         if len(ends)>=2:
            regex=r' '+re.escape(ends[0])+r' (.+?) '+re.escape(ends[1])+r' '
            x=re.search(regex,line)
            if x:
               found=x.group(1)
               #print(found)

               list2b.append(found)
               break
      else:
         
         for pattern in final_bucket2:
            ends=pattern.split()
            if len(ends)>=2:
               regex=r' '+re.escape(ends[0])+r' '+re.escape(ends[1])+r' (.*) '
               x=re.search(regex,line)
               if x:
                  found=x.group(1)
                  #print(found)

                  list2b.append(found)
      if found==0:
         list2b.append('Not Found')
      da_list.append(list2b)
         
    final_answer=zip(sentences,da_list)
    #print('_______________-')
    #print(final_answer)
    fully_final_list=[list(l) for l in final_answer]
    return fully_final_list

